00px;\n  margin: 0 auto;\n  padding: 0 2rem;\n}\n\n.features h2 {\n  text-align: center;\n  font-size: 2.5rem;\n  margin-bottom: 3rem;\n  color: #333;\n}\n\n.features-grid {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));\n  gap: 2rem;\n}\n\n.feature-card {\n  background: white;\n  padding: 2rem;\n  border-radius: 15px;\n  text-align: center;\n  box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n  transition: transform 0.3s;\n}\n\n.feature-card:hover {\n  transform: translateY(-5px);\n}\n\n.feature-icon {\n  font-size: 3rem;\n  margin-bottom: 1rem;\n}\n\n.feature-card h3 {\n  font-size: 1.5rem;\n  margin-bottom: 1rem;\n  color: #333;\n}\n\n.feature-card p {\n  color: #666;\n  line-height: 1.6;\n}\n\n.month-preview {\n  padding: 4rem 0;\n}\n\n.month-preview h2 {\n  text-align: center;\n  font-size: 2.5rem;\n  margin-bottom: 3rem;\n  color: #333;\n}\n\n.festivals-grid {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));\n  gap: 2rem;\n}\n\n.festival-card {\n  background: white;\n  padding: 2rem;\n  border-radius: 15px;\n  box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n  cursor: pointer;\n  transition: all 0.3s;\n  border: 2px solid transparent;\n}\n\n.festival-card:hover {\n  transform: translateY(-5px);\n  border-color: #667eea;\n  box-shadow: 0 10px 25px rgba(102, 126, 234, 0.2);\n}\n\n.festival-date {\n  background: linear-gradient(135deg, #667eea, #764ba2);\n  color: white;\n  padding: 0.5rem 1rem;\n  border-radius: 20px;\n  display: inline-block;\n  margin-bottom: 1rem;\n  font-weight: 600;\n}\n\n.festival-card h3 {\n  font-size: 1.5rem;\n  margin-bottom: 1rem;\n  color: #333;\n}\n\n.festival-card p {\n  color: #666;\n  line-height: 1.6;\n}\n\n@media (max-width: 768px) {\n  .hero h1 {\n    font-size: 2rem;\n  }\n  \n  .hero-description {\n    font-size: 1rem;\n  }\n  \n  .features-grid,\n  .festivals-grid {\n    grid-template-columns: 1fr;\n  }\n  \n  .container {\n    padding: 0 1rem;\n  }\n}\n</style>\n    ```\n\n\n\n\n[å·¥å…·è°ƒç”¨] æ­£åœ¨å†™å…¥æ–‡ä»¶\n\n\n\n[å·¥å…·è°ƒç”¨å®Œæˆ] å·²æˆåŠŸå†™å…¥æ–‡ä»¶ src/pages/Calendar.vue\n    ```vue\n    <template>\n  <div class=\"calendar-page\">\n    <div class=\"calendar-header\">\n      <h1>è‰ºæœ¯æ—¥å†</h1>\n      <div class=\"calendar-controls\">\n        <button @click=\"prevMonth\" class=\"nav-button\">â†</button>\n        <span class=\"current-month\">{{ currentMonth }} {{ currentYear }}</span>\n        <button @click=\"nextMonth\" class=\"nav-button\">â†’</button>\n      </div>\n    </div>\n    \n    <div class=\"calendar-grid\">\n      <div class=\"weekdays\">\n        <div v-for=\"day in weekdays\" :key=\"day\" class=\"weekday\">\n          {{ day }}\n        </div>\n      </div>\n      \n      <div class=\"days-grid\">\n        <div \n          v-for=\"day in calendarDays\" \n          :key=\"day.date\"\n          :class=\"[\'day-cell\', { \n            \'current-month\': day.isCurrentMonth,\n            \'today\': day.isToday,\n            \'has-festival\': day.hasFestival\n          }]\"\n          @click=\"selectDate(day)\"\n        >\n          <div class=\"day-number\">{{ day.day }}</div>\n          <div v-if=\"day.hasFestival\" class=\"festival-indicator\">ğŸ‰</div>\n          <div v-if=\"day.festivals\" class=\"festival-names\">\n            {{ day.festivals.join(\'ã€\') }}\n          </div>\n        </div>\n      </div>\n    </div>\n    \n    <div v-if=\"selectedDate\" class=\"selected-date-info\">\n      <h3>é€‰ä¸­æ—¥æœŸ: {{ selectedDate.date }}</h3>\n      <div v-if=\"selectedDate.festivals && selectedDate.festivals.length > 0\">\n        <h4>èŠ‚æ—¥:</h4>\n        <ul>\n          <li \n            v-for=\"festival in selectedDate.festivals\" \n            :key=\"festival\"\n            class=\"festival-item\"\n            @click=\"viewFestivalDetail(selectedDate.date, festival)\"\n          >\n            {{ festival }}\n          </li>\n        </ul>\n      </div>\n      <p v-else>è¿™ä¸€å¤©æ²¡æœ‰ç‰¹åˆ«çš„èŠ‚æ—¥</p>\n    </div>\n  </div>\n</template>\n\n<script setup>\nimport { ref, computed, onMounted } from \'vue\'\nimport { useRouter } from \'vue-router\'\n\nconst router = useRouter()\n\nconst currentDate = ref(new Date())\nconst selectedDate = ref(null)\n\nconst weekdays = [\'æ—¥\', \'ä¸€\', \'äºŒ\', \'ä¸‰\', \'å››\', \'äº”\', \'å…­\']\n\n// æ¨¡æ‹ŸèŠ‚æ—¥æ•°æ®\nconst festivalData = {\n  \'2024-12-25\': [\'åœ£è¯èŠ‚\'],\n  \'2024-12-31\': [\'é™¤å¤•\'],\n  \'2025-01-01\': [\'å…ƒæ—¦\'],\n  \'2025-02-10\': [\'æ˜¥èŠ‚\'],\n  \'2025-02-14\': [\'æƒ…äººèŠ‚\'],\n  \'2025-03-08\': [\'å¦‡å¥³èŠ‚\'],\n  \'2025-04-04\': [\'æ¸…æ˜èŠ‚\'],\n  \'2025-05-01\': [\'åŠ³åŠ¨èŠ‚\'],\n  \'2025-06-01\': [\'å„¿ç«¥èŠ‚\'],\n  \'2025-09-10\': [\'æ•™å¸ˆèŠ‚\'],\n  \'2025-10-01\': [\'å›½åº†èŠ‚\'],\n  \'2025-12-24\': [\'å¹³å®‰å¤œ\']\n}\n\nconst currentYear = computed(() => {\n  return currentDate.value.getFullYear()\n})\n\nconst currentMonth = computed(() => {\n  const months = [\'ä¸€æœˆ\', \'äºŒæœˆ\', \'ä¸‰æœˆ\', \'å››æœˆ\', \'äº”æœˆ\', \'å…­æœˆ\', \'ä¸ƒæœˆ\', \'å…«æœˆ\', \'ä¹æœˆ\', \'åæœˆ\', \'åä¸€æœˆ\', \'åäºŒæœˆ\']\n  return months[currentDate.value.getMonth()]\n})\n\nconst calendarDays = computed(() => {\n  const year = currentDate.value.getFullYear()\n  const month = currentDate.value.getMonth()\n  \n  // å½“æœˆç¬¬ä¸€å¤©\n  const firstDay = new Date(year, month, 1)\n  // å½“æœˆæœ€åä¸€å¤©\n  const lastDay = new Date(year, month + 1, 0)\n  \n  // å½“æœˆå¤©æ•°\n  const daysInMonth = lastDay.getDate()\n  // å½“æœˆç¬¬ä¸€å¤©æ˜¯æ˜ŸæœŸå‡ \n  const firstDayIndex = firstDay.getDay()\n  \n  const days = []\n  \n  // æ·»åŠ ä¸Šä¸ªæœˆçš„æœ€åå‡ å¤©\n  const prevMonthLastDay = new Date(year, month, 0).getDate()\n  for (let i = firstDayIndex; i > 0; i--) {\n    const day = prevMonthLastDay - i + 1\n    days.push({\n      day,\n      date: formatDate(new Date(year, month - 1, day)),\n      isCurrentMonth: false,\n      isToday: false,\n      hasFestival: false\n    })\n  }\n  \n  // æ·»åŠ å½“æœˆçš„å¤©æ•°\n  const today = new Date()\n  for (let i = 1; i <= daysInMonth; i++) {\n    const date = new Date(year, month, i)\n    const dateStr = formatDate(date)\n    const festivals = festivalData[dateStr]\n    const hasFestival = !!festivals\n    \n    days.push({\n      day: i,\n      date: dateStr,\n      isCurrentMonth: true,\n      isToday: formatDate(today) === dateStr,\n      hasFestival,\n      festivals\n    })\n  }\n  \n  // æ·»åŠ ä¸‹ä¸ªæœˆçš„å‰å‡ å¤©\n  const daysNeeded = 42 - days.length // 6è¡Œ7åˆ—\n  for (let i = 1; i <= daysNeeded; i++) {\n    days.push({\n      day: i,\n      date: formatDate(new Date(year, month + 1, i)),\n      isCurrentMonth: false,\n      isToday: false,\n      hasFestival: false\n    })\n  }\n  \n  return days\n})\n\nfunction formatDate(date) {\n  const year = date.getFullYear()\n  const month = (date.getMonth() + 1).toString().padStart(2, \'0\')\n  const day = date.getDate().toString().padStart(2, \'0\')\n  return `${year}-${month}-${day}`\n}\n\nfunction prevMonth() {\n  currentDate.value = new Date(currentDate.value.getFullYear(), currentDate.value.getMonth() - 1, 1)\n}\n\nfunction nextMonth() {\n  currentDate.value = new Date(currentDate.value.getFullYear(), currentDate.value.getMonth() + 1, 1)\n}\n\nfunction selectDate(day) {\n  if (day.isCurrentMonth) {\n    selectedDate.value = day\n  }\n}\n\nfunction viewFestivalDetail(date, festival) {\n  router.push(`/festival/${date}`)\n}\n\nonMounted(() => {\n  // é»˜è®¤é€‰ä¸­ä»Šå¤©\n  const today = formatDate(new Date())\n  const todayDay = calendarDays.value.find(day => day.date === today)\n  if (todayDay) {\n    selectedDate.value = todayDay\n  }\n})\n</script>\n\n<style scoped>\n.calendar-page {\n  max-width: 1200px;\n  margin: 0 auto;\n  padding: 2rem;\n}\n\n.calendar-header {\n  text-align: center;\n  margin-bottom: 2rem;\n}\n\n.calendar-header h1 {\n  font-size: 2.5rem;\n  color: #333;\n  margin-bottom: 1rem;\n}\n\n.calendar-controls {\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  gap: 1rem;\n}\n\n.nav-button {\n  background: linear-gradient(135deg, #667eea, #764ba2);\n  color: white;\n  border: none;\n  padding: 0.5rem 1rem;\n  border-radius: 8px;\n  cursor: pointer;\n  font-size: 1.2rem;\n  transition: opacity 0.3s;\n}\n\n.nav-button:hover {\n  opacity: 0.8;\n}\n\n.current-month {\n  font-size: 1.5rem;\n  font-weight: 600;\n  color: #333;\n  min-width: 200px;\n  text-align: center;\n}\n\n.calendar-grid {\n  background: white;\n  border-radius: 15px;\n  box-shadow: 0 10px 30px rgba(0,0,0,0.1);\n  overflow: hidden;\n}\n\n.weekdays {\n  display: grid;\n  grid-template-columns: repeat(7, 1fr);\n  background: linear-gradient(135deg, #667eea, #764ba2);\n  color: white;\n}\n\n.weekday {\n  padding: 1rem;\n  text-align: center;\n  font-weight: 600;\n}\n\n.days-grid {\n  display: grid;\n  grid-template-columns: repeat(7, 1fr);\n  gap: 1px;\n  background: #e9ecef;\n}\n\n.day-cell {\n  background: white;\n  min-height: 100px;\n  padding: 0.5rem;\n  cursor: pointer;\n  transition: all 0.3s;\n  position: relative;\n}\n\n.day-cell:hover {\n  background: #f8f9fa;\n  transform: scale(1.02);\n  z-index: 1;\n  box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n}\n\n.day-cell.current-month {\n  background: white;\n}\n\n.day-cell:not(.current-month) {\n  background: #f8f9fa;\n  color: #adb5bd;\n}\n\n.day-cell.today {\n  background: #e3f2fd;\n  border: 2px solid #2196f3;\n}\n\n.day-cell.has-festival {\n  background: linear-gradient(135deg, #fff3cd, #ffeaa7);\n}\n\n.day-number {\n  font-size: 1.2rem;\n  font-weight: 600;\n  margin-bottom: 0.5rem;\n}\n\n.festival-indicator {\n  position: absolute;\n  top: 5px;\n  right: 5px;\n  font-size: 0.8rem;\n}\n\n.festival-names {\n  font-size: 0.7rem;\n  color: #666;\n  line-height: 1.2;\n  overflow: hidden;\n  text-overflow: ellipsis;\n  display: -webkit-box;\n  -webkit-line-clamp: 2;\n  -webkit-box-orient: vertical;\n}\n\n.selected-date-info {\n  margin-top: 2rem;\n  padding: 2rem;\n  background: white;\n  border-radius: 15px;\n  box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n}\n\n.selected-date-info h3 {\n  color: #333;\n  margin-bottom: 1rem;\n}\n\n.selected-date-info h4 {\n  color: #666;\n  margin-bottom: 0.5rem;\n}\n\n.festival-item {\n  padding: 0.5rem;\n  margin: 0.5rem 0;\n  background: #f8f9fa;\n  border-radius: 8px;\n  cursor: pointer;\n  transition: background 0.3s;\n}\n\n.festival-item:hover {\n  background: #e3f2fd;\n}\n\n@media (max-width: 768px) {\n  .calendar-page {\n    padding: 1rem;\n  }\n  \n  .day-cell {\n    min-height: 80px;\n    padding: 0.3rem;\n  }\n  \n  .day-number {\n    font-size: 1rem;\n  }\n  \n  .festival-names {\n    font-size: 0.6rem;\n    -webkit-line-clamp: 1;\n  }\n}\n</style>\n    ```\n\n\n\n\n[å·¥å…·è°ƒç”¨] æ­£åœ¨å†™å…¥æ–‡ä»¶\n\n\n\n[å·¥å…·è°ƒç”¨å®Œæˆ] å·²æˆåŠŸå†™å…¥æ–‡ä»¶ src/pages/FestivalDetail.vue\n    ```vue\n    <template>\n  <div class=\"festival-detail\">\n    <div class=\"detail-header\">\n      <button @click=\"goBack\" class=\"back-button\">â† è¿”å›</button>\n      <h1>{{ festivalInfo.name }}</h1>\n      <div class=\"festival-date\">{{ formattedDate }}</div>\n    </div>\n    \n    <div class=\"detail-content\">\n      <div class=\"festival-image\">\n        <img :src=\"festivalImage\" :alt=\"festivalInfo.name\" />\n      </div>\n      \n      <div class=\"festival-info\">\n        <div class=\"info-section\">\n          <h2>èŠ‚æ—¥ä»‹ç»</h2>\n          <p>{{ festivalInfo.description }}</p>\n        </div>\n        \n        <div class=\"info-section\">\n          <h2>ä¼ ç»Ÿä¹ ä¿—</h2>\n          <ul>\n            <li v-for=\"(custom, index) in festivalInfo.customs\" :key=\"index\">\n              {{ custom }}\n            </li>\n          </ul>\n        </div>\n        \n        <div class=\"info-section\">\n          <h2>èŠ‚æ—¥ç¾é£Ÿ</h2>\n          <div class=\"foods-grid\">\n            <div \n              v-for=\"(food, index) in festivalInfo.foods\" \n              :key=\"index\"\n              class=\"food-item\"\n            >\n              <span class=\"food-icon\">ğŸ½ï¸</span>\n              <span class=\"food-name\">{{ food }}</span>\n            </div>\n          </div>\n        </div>\n        \n        <div class=\"info-section\">\n          <h2>ç›¸å…³æ´»åŠ¨</h2>\n          <p>{{ festivalInfo.activities }}</p>\n        </div>\n      </div>\n    </div>\n    \n    <div class=\"related-festivals\">\n      <h2>ç›¸å…³èŠ‚æ—¥</h2>\n      <div class=\"related-grid\">\n        <div \n          v-for=\"related in relatedFestivals\" \n          :key=\"related.date\"\n          class=\"related-card\"\n          @click=\"goToFestival(related.date)\"\n        >\n          <div class=\"related-date\">{{ related.date }}</div>\n          <h3>{{ related.name }}</h3>\n        </div>\n      </div>\n    </div>\n  </div>\n</template>\n\n<script setup>\nimport { computed, onMounted } from \'vue\'\nimport { useRouter, useRoute } from \'vue-router\'\n\nconst router = useRouter()\nconst route = useRoute()\n\nconst festivalDatabase = {\n  \'2024-12-25\': {\n    name: \'åœ£è¯èŠ‚\',\n    description: \'åœ£è¯èŠ‚æ˜¯çºªå¿µè€¶ç¨£åŸºç£è¯ç”Ÿçš„ä¼ ç»ŸèŠ‚æ—¥ï¼Œèµ·æºäºåŸºç£æ•™ï¼Œç°åœ¨å·²æˆä¸ºå…¨çƒæ€§çš„æ–‡åŒ–åº†å…¸ã€‚è¿™ä¸€å¤©è±¡å¾ç€å’Œå¹³ã€çˆ±ä¸å¸Œæœ›ï¼Œäººä»¬ä¼šè£…é¥°åœ£è¯æ ‘ã€äº¤æ¢ç¤¼ç‰©ã€å”±åœ£è¯é¢‚æ­Œï¼Œå…±åŒåº†ç¥è¿™ä¸ªå……æ»¡æ¬¢ä¹çš„èŠ‚æ—¥ã€‚\',\n    customs: [\n      \'è£…é¥°åœ£è¯æ ‘å’Œå®¶å±…\',\n      \'äº¤æ¢åœ£è¯ç¤¼ç‰©\',\n      \'å”±åœ£è¯é¢‚æ­Œ\',\n      \'å‚åŠ åœ£è¯å¼¥æ’’\',\n      \'å‡†å¤‡åœ£è¯å¤§é¤\'\n    ],\n    foods: [\n      \'çƒ¤ç«é¸¡\',\n      \'åœ£è¯å¸ƒä¸\',\n      \'å§œé¥¼äºº\',\n      \'çƒ­çº¢é…’\',\n      \'åœ£è¯è›‹ç³•\'\n    ],\n    activities: \'å®¶åº­èšä¼šã€æ•™å ‚ç¤¼æ‹œã€åœ£è¯æ¸¸è¡Œã€æ…ˆå–„æ´»åŠ¨ç­‰ã€‚å­©å­ä»¬ä¼šæœŸå¾…åœ£è¯è€äººçš„ç¤¼ç‰©ï¼Œæˆå¹´äººåˆ™äº«å—ä¸å®¶äººæœ‹å‹å…±åº¦çš„æ¸©é¦¨æ—¶å…‰ã€‚\'\n  },\n  \'2024-12-31\': {\n    name: \'é™¤å¤•\',\n    description: \'é™¤å¤•æ˜¯ä¸€å¹´çš„æœ€åä¸€å¤©ï¼Œä¹Ÿæ˜¯è¿æ¥æ–°å¹´çš„é‡è¦æ—¶åˆ»ã€‚åœ¨ä¸­å›½ä¼ ç»Ÿæ–‡åŒ–ä¸­ï¼Œé™¤å¤•å¤œå®¶äººå›¢èšï¼Œåƒå¹´å¤œé¥­ï¼Œå®ˆå²è¿æ–°ï¼Œå¯“æ„ç€è¾æ—§è¿æ–°ã€ç¥ˆç¦çº³ç¥¥ã€‚\',\n    customs: [\n      \'åƒå¹´å¤œé¥­\',\n      \'è´´æ˜¥è”çª—èŠ±\',\n      \'å®ˆå²è¿æ–°\',\n      \'å‘å‹å²é’±\',\n      \'æ”¾é­ç‚®çƒŸèŠ±\'\n    ],\n    foods: [\n      \'é¥ºå­\',\n      \'å¹´ç³•\',\n      \'é±¼\',\n      \'æ±¤åœ†\',\n      \'æ˜¥å·\'\n    ],\n    activities: \'å®¶åº­å›¢èšã€è§‚çœ‹æ˜¥æ™šã€ç¥­ç¥€ç¥–å…ˆã€æ‹œå¹´ç¥ç¦ç­‰ã€‚åˆå¤œæ—¶åˆ†ï¼Œäººä»¬ä¼šæ”¾é­ç‚®è¿æ¥æ–°å¹´çš„åˆ°æ¥ã€‚\'\n  },\n  \'2025-01-01\': {\n    name: \'å…ƒæ—¦\',\n    description: \'å…ƒæ—¦æ˜¯æ–°å¹´çš„ç¬¬ä¸€å¤©ï¼Œè±¡å¾ç€æ–°çš„å¼€å§‹å’Œå¸Œæœ›ã€‚å…¨çƒå„åœ°éƒ½ä¼šä¸¾è¡Œå„ç§åº†ç¥æ´»åŠ¨ï¼Œäººä»¬åˆ¶å®šæ–°å¹´è®¡åˆ’ï¼ŒæœŸå¾…æ–°çš„ä¸€å¹´å¸¦æ¥å¥½è¿å’ŒæˆåŠŸã€‚\',\n    customs: [\n      \'è§‚çœ‹çƒŸèŠ±è¡¨æ¼”\',\n      \'åˆ¶å®šæ–°å¹´è®¡åˆ’\',\n      \'å‘é€æ–°å¹´ç¥ç¦\',\n      \'å‚åŠ åº†ç¥æ´»åŠ¨\',\n      \'å®¶åº­èšé¤\'\n    ],\n    foods: [\n      \'æ–°å¹´è›‹ç³•\',\n      \'å¹¸è¿é£Ÿç‰©\',\n      \'ä¼ ç»Ÿç¾é£Ÿ\',\n      \'èŠ‚æ—¥ç”œç‚¹\',\n      \'ç‰¹è‰²é¥®å“\'\n    ],\n    activities: \'è·¨å¹´å€’è®¡æ—¶ã€çƒŸèŠ±è¡¨æ¼”ã€éŸ³ä¹ä¼šã€æ¸¸è¡Œåº†ç¥ç­‰ã€‚è®¸å¤šäººä¼šé€‰æ‹©åœ¨è¿™ä¸€å¤©å¼€å§‹å®æ–½æ–°çš„ç›®æ ‡å’Œè®¡åˆ’ã€‚\'\n  },\n  \'2025-02-10\': {\n    name: \'æ˜¥èŠ‚\',\n    description: \'æ˜¥èŠ‚æ˜¯ä¸­å›½æœ€é‡è¦çš„ä¼ ç»ŸèŠ‚æ—¥ï¼Œåˆç§°å†œå†æ–°å¹´ã€‚è±¡å¾ç€ä¸‡ç‰©å¤è‹ã€æ–°çš„å¼€å§‹ï¼Œæ˜¯å®¶äººå›¢èšã€ç¥­ç¥–ç¥ˆç¦çš„é‡è¦æ—¶åˆ»ã€‚\',\n    customs: [\n      \'è´´æ˜¥è”ç¦å­—\',\n      \'æ‹œå¹´èµ°äº²\',\n      \'èˆé¾™èˆç‹®\',\n      \'ç¥­ç¥–ç¥ˆç¦\',\n      \'å‘çº¢åŒ…\'\n    ],\n    foods: [\n      \'å¹´å¤œé¥­\',\n      \'é¥ºå­\',\n      \'å¹´ç³•\',\n      \'æ±¤åœ†\',\n      \'æ˜¥å·\'\n    ],\n    activities: \'å®¶åº­å›¢èšã€åº™ä¼šæ¸¸å›­ã€æ–‡è‰ºè¡¨æ¼”ã€ä¼ ç»Ÿæ¸¸æˆç­‰ã€‚æ•´ä¸ªåº†ç¥æ´»åŠ¨æŒç»­15å¤©ï¼Œç›´åˆ°å…ƒå®µèŠ‚ã€‚\'\n  }\n}\n\nconst festivalInfo = computed(() => {\n  return festivalDatabase[route.params.date] || {\n    name: \'æœªçŸ¥èŠ‚æ—¥\',\n    description: \'æš‚æ— è¯¥èŠ‚æ—¥çš„è¯¦ç»†ä¿¡æ¯\',\n    customs: [],\n    foods: [],\n    activities: \'\'\n  }\n})\n\nconst formattedDate = computed(() => {\n  const dateStr = route.params.date\n  if (dateStr) {\n    const [year, month, day] = dateStr.split(\'-\')\n    return `${year}å¹´${month}æœˆ${day}æ—¥`\n  }\n  return \'\'\n})\n\nconst festivalImage = computed(() => {\n  return `https://picsum.photos/800/400?random=${Math.random()}`\n})\n\nconst relatedFestivals = [\n  { date: \'2024-12-24\', name: \'å¹³å®‰å¤œ\' },\n  { date: \'2025-02-14\', name: \'æƒ…äººèŠ‚\' },\n  { date: \'2025-04-04\', name: \'æ¸…æ˜èŠ‚\' },\n  { date: \'2025-10-01\', name: \'å›½åº†èŠ‚\' }\n]\n\nfunction goBack() {\n  router.back()\n}\n\nfunction goToFestival(date) {\n  router.push(`/festival/${date}`)\n}\n\nonMounted(() => {\n  if (!festivalDatabase[route.params.date]) {\n    console.warn(\'æœªæ‰¾åˆ°è¯¥æ—¥æœŸçš„èŠ‚æ—¥ä¿¡æ¯\')\n  }\n})\n</script>\n\n<style scoped>\n.festival-detail {\n  max-width: 1000px;\n  margin: 0 auto;\n  padding: 2rem;\n}\n\n.detail-header {\n  text-align: center;\n  margin-bottom: 3rem;\n  position: relative;\n}\n\n.back-button {\n  position: absolute;\n  left: 0;\n  top: 0;\n  background: #6c757d;\n  color: white;\n  border: none;\n  padding: 0.5rem 1rem;\n  border-radius: 8px;\n  cursor: pointer;\n  transition: background 0.3s;\n}\n\n.back-button:hover {\n  background: #5a6268;\n}\n\n.detail-header h1 {\n  font-size: 3rem;\n  color: #333;\n  margin-bottom: 1rem;\n}\n\n.festival-date {\n  font-size: 1.5rem;\n  color: #667eea;\n  font-weight: 600;\n}\n\n.detail-content {\n  display: grid;\n  grid-template-columns: 1fr 1fr;\n  gap: 3rem;\n  margin-bottom: 3rem;\n}\n\n.festival-image img {\n  width: 100%;\n  height: 300px;\n  object-fit: cover;\n  border-radius: 15px;\n  box-shadow: 0 10px 30px rgba(0,0,0,0.2);\n}\n\n.festival-info {\n  display: flex;\n  flex-direction: column;\n  gap: 2rem;\n}\n\n.info-section h2 {\n  color: #333;\n  margin-bottom: 1rem;\n  font-size: 1.5rem;\n  border-bottom: 2px solid #667eea;\n  padding-bottom: 0.5rem;\n}\n\n.info-section p {\n  color: #666;\n  line-height: 1.8;\n  font-size: 1.1rem;\n}\n\n.info-section ul {\n  color: #666;\n  line-height: 1.8;\n  padding-left: 1.5rem;\n}\n\n.info-section li {\n  margin-bottom: 0.5rem;\n}\n\n.foods-grid {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));\n  gap: 1rem;\n}\n\n.food-item {\n  display: flex;\n  align-items: center;\n  gap: 0.5rem;\n  padding: 0.5rem;\n  background: #f8f9fa;\n  border-radius: 8px;\n  transition: background 0.3s;\n}\n\n.food-item:hover {\n  background: #e3f2fd;\n}\n\n.food-icon {\n  font-size: 1.2rem;\n}\n\n.food-name {\n  color: #333;\n  font-weight: 500;\n}\n\n.related-festivals {\n  margin-top: 3rem;\n}\n\n.related-festivals h2 {\n  text-align: center;\n  font-size: 2rem;\n  color: #333;\n  margin-bottom: 2rem;\n}\n\n.related-grid {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));\n  gap: 1.5rem;\n}\n\n.related-card {\n  background: white;\n  padding: 1.5rem;\n  border-radius: 12px;\n  box-shadow: 0 5px 15px rgba(0,0,0,0.1);\n  cursor: pointer;\n  transition: all 0.3s;\n  text-align: center;\n}\n\n.related-card:hover {\n  transform: translateY(-5px);\n  box-shadow: 0 10px 25px rgba(102, 126, 234, 0.2);\n}\n\n.related-date {\n  background: linear-gradient(135deg, #667eea, #764ba2);\n  color: white;\n  padding: 0.3rem 0.8rem;\n  border-radius: 15px;\n  font-size: 0.9rem;\n  margin-bottom: 1rem;\n  display: inline-block;\n}\n\n.related-card h3 {\n  color: #333;\n  font-size: 1.2rem;\n  margin: 0;\n}\n\n@media (max-width: 768px) {\n  .festival-detail {\n    padding: 1rem;\n  }\n  \n  .detail-header h1 {\n    font-size: 2rem;\n  }\n  \n  .detail-content {\n    grid-template-columns: 1fr;\n    gap: 2rem;\n  }\n  \n  .festival-image img {\n    height: 200px;\n  }\n  \n  .related-grid {\n    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));\n  }\n  \n  .back-button {\n    position: relative;\n    margin-bottom: 1rem;\n  }\n}\n</style>\n    ```\n\n\n\n\n[å·¥å…·è°ƒç”¨] æ­£åœ¨å†™å…¥æ–‡ä»¶\n\n\n\n[å·¥å…·è°ƒç”¨å®Œæˆ] å·²æˆåŠŸå†™å…¥æ–‡ä»¶ src/components/CalendarComponent.vue\n    ```vue\n    <template>\n  <div class=\"calendar-component\">\n    <div class=\"calendar-header\">\n      <button @click=\"prevMonth\" class=\"nav-button\">â†</button>\n      <span class=\"current-month\">{{ currentMonth }} {{ currentYear }}</span>\n      <button @click=\"nextMonth\" class=\"nav-button\">â†’</button>\n    </div>\n    \n    <div class=\"calendar-grid\">\n      <div class=\"weekdays\">\n        <div v-for=\"day in weekdays\" :key=\"day\" class=\"weekday\">\n          {{ day }}\n        </div>\n      </div>\n      \n      <div class=\"days-grid\">\n        <div \n          v-for=\"day in calendarDays\" \n          :key=\"day.date\"\n          :class=\"[\'day-cell\', { \n            \'current-month\': day.isCurrentMonth,\n            \'today\': day.isToday,\n            \'has-festival\': day.hasFestival\n          }]\"\n          @click=\"$emit(\'date-selected\', day)\"\n        >\n          <div class=\"day-number\">{{ day.day }}</div>\n          <div v-if=\"day.hasFestival\" class=\"festival-indicator\">ğŸ‰</div>\n        </div>\n      </div>\n    </div>\n  </div>\n</template>\n\n<script setup>\nimport { ref, computed } from \'vue\'\n\nconst emit = defineEmits([\'date-selected\'])\n\nconst currentDate = ref(new Date())\nconst weekdays = [\'æ—¥\', \'ä¸€\', \'äºŒ\', \'ä¸‰\', \'å››\', \'äº”\', \'å…­\']\n\n// æ¨¡æ‹ŸèŠ‚æ—¥æ•°æ®\nconst festivalData = {\n  \'2024-12-25\': [\'åœ£è¯èŠ‚\'],\n  \'2024-12-31\': [\'é™¤å¤•\'],\n  \'2025-01-01\': [\'å…ƒæ—¦\']\n}\n\nconst currentYear = computed(() => {\n  return currentDate.value.getFullYear()\n})\n\nconst currentMonth = computed(() => {\n  const months = [\'ä¸€æœˆ\', \'äºŒæœˆ\', \'ä¸‰æœˆ\', \'å››æœˆ\', \'äº”æœˆ\', \'å…­æœˆ\', \'ä¸ƒæœˆ\', \'å…«æœˆ\', \'ä¹æœˆ\', \'åæœˆ\', \'åä¸€æœˆ\', \'åäºŒæœˆ\']\n  return months[currentDate.value.getMonth()]\n})\n\nconst calendarDays = computed(() => {\n  const year = currentDate.value.getFullYear()\n  const month = currentDate.value.getMonth()\n  \n  const firstDay = new Date(year, month, 1)\n  const lastDay = new Date(year, month + 1, 0)\n  const daysInMonth = lastDay.getDate()\n  const firstDayIndex = firstDay.getDay()\n  \n  const days = []\n  \n  // æ·»åŠ ä¸Šä¸ªæœˆçš„æœ€åå‡ å¤©\n  const prevMonthLastDay = new Date(year, month, 0).getDate()\n  for (let i = firstDayIndex; i > 0; i--) {\n    const day = prevMonthLastDay - i + 1\n    days.push({\n      day,\n      date: formatDate(new Date(year, month - 1, day)),\n      isCurrentMonth: false,\n      isToday: false,\n      hasFestival: false\n    })\n  }\n  \n  // æ·»åŠ å½“æœˆçš„å¤©æ•°\n  const today = new Date()\n  for (let i = 1; i <= daysInMonth; i++) {\n    const date = new Date(year, month, i)\n    const dateStr = formatDate(date)\n    const hasFestival = !!festivalData[dateStr]\n    \n    days.push({\n      day: i,\n      date: dateStr,\n      isCurrentMonth: true,\n      isToday: formatDate(today) === dateStr,\n      hasFestival\n    })\n  }\n  \n  // æ·»åŠ ä¸‹ä¸ªæœˆçš„å‰å‡ å¤©\n  const daysNeeded = 42 - days.length\n  for (let i = 1; i <= daysNeeded; i++) {\n    days.push({\n      day: i,\n      date: formatDate(new Date(year, month + 1, i)),\n      isCurrentMonth: false,\n      isToday: false,\n      hasFestival: false\n    })\n  }\n  \n  return days\n})\n\nfunction formatDate(date) {\n  const year = date.getFullYear()\n  const month = (date.getMonth() + 1).toString().padStart(2, \'0\')\n  const day = date.getDate().toString().padStart(2, \'0\')\n  return `${year}-${month}-${day}`\n}\n\nfunction prevMonth() {\n  currentDate.value = new Date(currentDate.value.getFullYear(), currentDate.value.getMonth() - 1, 1)\n}\n\nfunction nextMonth() {\n  currentDate.value = new Date(currentDate.value.getFullYear(), currentDate.value.getMonth() + 1, 1)\n}\n</script>\n\n<style scoped>\n.calendar-component {\n  background: white;\n  border-radius: 15px;\n  box-shadow: 0 10px 30px rgba(0,0,0,0.1);\n  overflow: hidden;\n}\n\n.calendar-header {\n 